    {
        [OutputType([hashtable])] 
        param (
            [Parameter(Mandatory = $true,
            ValueFromPipeline = $true)]
        [psobject]$jsonObj
        )
        $hash = @{ }
        foreach ($property in $jsonObj.PSObject.Properties)
        {
            $hash[$property.Name] = $property.Value
        }
        $hash
    }
