    Get-Item . |
    Select-Object -ExpandProperty property | 
    ForEach-Object {
        New-Object -TypeName psobject -Property @{
            "property" = $_;
            "Value" = (Get-ItemProperty -Path . -Name $_).$_
        }
    }
    Pop-Location        
